mapper.select:
SELECT ar.result_id, ar.assay_no, ar.result_no, ar.sample_no,
Round( ar.sample_volume, 3 ) sample_volume, ar.device_no, ar.assay_item, Round( ar.result_concentration, 3 ) result_concentration, ar.result_abs, ar.result_date, ar.result_time, ar.curve_k0, ar.curve_k1, ar.curve_id, ar.create_by, ar.create_time, ar.update_by, ar.update_time, ar.assay_type, sa.item_name AS assay_sample_name, t.item_name, s.sample_id, s.sample_name, w.works_id, w.works_name, w.works_no, ar2.avg_result_concentration, ar2.type_num FROM z_assay_result ar LEFT JOIN ( SELECT rin.assay_type, rin.result_date, rin.assay_no, rin.assay_item, Round(AVG( rin.result_concentration ), 3) avg_result_concentration, COUNT(rin.assay_type) AS type_num FROM z_assay_result rin WHERE 1 = 1 AND rin.assay_no = #{assayNo} AND rin.assay_item = #{assayItem} GROUP BY rin.assay_type, rin.result_date, rin.assay_no, rin.assay_item ) AS ar2 ON ar.assay_no = ar2.assay_no AND ar.assay_type = ar2.assay_type LEFT JOIN sys_assay_item t ON t.assay_item = ar.assay_item LEFT JOIN sys_assay_sample sa ON ar.assay_type = sa.assay_type LEFT JOIN z_assay_sample s ON s.sample_no = ar.sample_no AND s.assay_no = ar.assay_no LEFT JOIN biz_water_work w ON s.work_no = w.works_no WHERE 1=1 and ar.result_id = #{resultId}
AND ar.assay_no = #{assayNo} and ar.device_no = #{deviceNo} and ar.assay_item = #{assayItem} and ar.result_date = #{resultDate}
AND datediff(dd,#{params.beginTime},ar.result_time) >=0
AND datediff(dd,ar.result_time,#{params.endTime}) >=0
${params.dataScope} order by ar.result_date desc


where 1=1
     and r.result_id = #{resultId}

         AND r.assay_no = #{assayNo}
      and r.device_no = #{deviceNo}
       and r.assay_item = #{assayItem}
      and r.result_date = #{resultDate}

        AND datediff(dd,#{params.beginTime},r.result_time) >=0


        AND datediff(dd,r.result_time,#{params.endTime}) >=0


${params.dataScope}
order by r.result_date desc

mapper.update:
update z_assay_result
assay_no = #{assayNo}, result_no = #{resultNo}, sample_no = #{sampleNo}, sample_volume = #{sampleVolume}, device_no = #{deviceNo}, assay_item = #{assayItem}, result_concentration = #{resultConcentration}, result_abs = #{resultAbs}, result_date = #{resultDate}, result_time = #{resultTime}, curve_k0 = #{curveK0}, curve_k1 = #{curveK1}, curve_id = #{curveId}, assay_type = #{assayType}, update_by = #{updateBy}, update_time = getdate()
where assay_id = #{assayId}

mapper.select:
SELECT t.assay_item assayItem, t.item_name itemName FROM sys_assay_item t
where t.assay_item in (
SELECT DISTINCT z.assay_item from z_assay_result z
where z.assay_no = #{assayNo}
)
